import hashlib

def generate_flag_with_salt(flag, salt):
    # Concatenate the flag and salt
    concatenated_string = flag + salt

    # Generate the hash using SHA256 algorithm
    hash_object = hashlib.sha256(concatenated_string.encode())

    # Get the hexadecimal representation of the hash
    hashed_string = hash_object.hexdigest()

    return hashed_string

# Example usage
flag = "my_flag"
salt = "somesalt"

hashed_flag = generate_flag_with_salt(flag, salt)
print("Hashed Flag:", hashed_flag)
